    private void hasilButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hasilButtonActionPerformed
        int Vnol=Integer.parseInt(Vnol2.getText());
        int Vt=Integer.parseInt(Vt2.getText());
        int t=Integer.parseInt(waktu.getText());
        double hasil=(Vt-Vnol)/(t*1.0); // rumus A= (Vt â€“ Vo) / t
        System.out.println("hasil : "+hasil);
        hasilText.setText("Hasil perhitungan adalah : "+String.valueOf(hasil));
    }//GEN-LAST:event_hasilButtonActionPerformed

